function getAllPropertyNames(obj) {
	var props = [];

	var getProperties = function(){
		Object.getOwnPropertyNames(obj).forEach(function(prop) {
			if (props.indexOf(prop) === -1) {
				props.push(prop);
			}
		});
	};

	var objectPrototype;
	do {
		getProperties();
		objectPrototype = Object.getPrototypeOf(obj);
	} while (objectPrototype);

	return props;
}

